function buildCard(e){let n="",t="";return t=`<p class="yellow-text">DURACIÓN: <span class="white-text">${e.duracion}</span></p>`,"Serie"==e.categoria&&(t=`<p class="yellow-text">TEMPORADAS: <span class="white-text">${e.temporadas}</span></p>`),n+=`<div class="col s12 m6 l3">\n                      <div class="card z-depth-2 max-wide">\n                         <div class="card-image">\n                            <img loading="lazy" src="${e.poster}" alt="${e.titulo}" title="${e.titulo}" id="trailerflix-${e.id}" width="440">\n                            <a class="btn-floating halfway-fab waves-effect waves-light red darken-2"><i class="material-icons"  onclick="verDetalle(${e.id})">search</i></a>\n                         </div>\n                         <div class="card-content black">\n                            <p class="yellow-text ">GÉNERO: <span class="white-text">${e.gen}</span></p>\n                            ${t}\n                         </div>\n                      </div>\n                   </div>`,n}function errorJSON(){let e="";return e='<div class="center white-text"> \n                      <br><br> \n                      <h2>El contenido no está disponible. Intente nuevamente en unos minutos.</h2> \n                      <br><br> \n                      <i class="large material-icons">sentiment_very_dissatisfied</i> \n                      <br><br> \n                   </div>','<div class="center white-text"> \n                      <br><br> \n                      <h2>El contenido no está disponible. Intente nuevamente en unos minutos.</h2> \n                      <br><br> \n                      <i class="large material-icons">sentiment_very_dissatisfied</i> \n                      <br><br> \n                   </div>'}